select

from lottery_win


		AND WINAID = #{page_object.winaid,jdbcType=BIGINT}


		AND USERNAME = #{page_object.username,jdbcType=VARCHAR}


		AND USERID = #{page_object.userid,jdbcType=BIGINT}


		AND LOTTERYNAME = #{page_object.lotteryname,jdbcType=VARCHAR}


		AND PID = #{page_object.pid,jdbcType=BIGINT}


		AND USERPHONE = #{page_object.userphone,jdbcType=VARCHAR}


		AND WINORFAILURE = #{page_object.winorfailure,jdbcType=CHAR}


		AND   WINDATE >= #{windateStart,jdbcType=TIMESTAMP} 


		AND   WINDATE <= #{windateEnd,jdbcType=TIMESTAMP} 


		AND     WINDATE = #{windateEqual,jdbcType=TIMESTAMP} 


		AND CHECKOUT = #{page_object.checkout,jdbcType=DECIMAL}


		AND PHONEINT = #{page_object.phoneint,jdbcType=VARCHAR}


		AND RECEIVE_NAME = #{page_object.receiveName,jdbcType=VARCHAR}


		AND SENDADDRESS = #{page_object.sendaddress,jdbcType=VARCHAR}


		AND REAL_NAME = #{page_object.realName,jdbcType=VARCHAR}


		AND SEND_POST = #{page_object.sendPost,jdbcType=VARCHAR}


		AND STATE = #{page_object.state,jdbcType=CHAR}


		AND ALIPAY = #{page_object.alipay,jdbcType=VARCHAR}


		AND PAYSEQNUM = #{page_object.payseqnum,jdbcType=VARCHAR}


		AND COMMENTS = #{page_object.comments,jdbcType=VARCHAR}


		AND ACTIVITY_ID = #{page_object.activityId,jdbcType=VARCHAR}

